import { Token } from "graphql";
import React, {useState, useEffect } from "react";
import { StyleSheet, View, TextInput, Button, Text, FlatList, Switch } from 'react-native';
import { createTask, taskList, deleteTask, checkAll, unCheckAll, doneTasks, undoneTasks } from "../API/todoAPI";

import todoData from '../Helpers/todoData';
import TodoItem from './TodoItem';
import { TokenContext, UsernameContext } from "../Context/Context";



export default function TodoList(){
   /** initialiser todos afin de pouvoir l'utiliser avec les changements d'états*/
   const [todos, setTodos]= useState([])
   const [todosScreen, setTodosScreen] = useState([])
   const [count, setCount]= useState(todos.filter(item => item.done).length)
   const [newTodoText, setNewTodoText] = useState("")
   const token = React.useContext(TokenContext)
   const username = React.useContext(UsernameContext)

   useEffect(() => {
      getTaskList()
   } ,[] 
   );

   function getTaskList() {

      taskList(username[0], token[0]).then(data => {
         if (JSON.stringify(todos) != JSON.stringify(data) ) {
            setTodos(data);
            setTodosScreen(data) ;
            setCount(data.filter(item => item.done).length)
            console.log(data)
         }

      })
   }

   const updateCountItemDone = (id) =>{
      /**compte les fonctions realisées et actualise au fur et à mesure */
      const newTodos = todos.map((todo) => {
      if (todo.id === id) {
         todo.done = !todo.done;
      } 
      return todo;
      });

      setCount(newTodos.filter(item => item.done).length);

   }

   const addNewTodo = () =>{
      /**ajoute un nouvel item à notre liste de taches */
      const newTask = createTask(newTodoText,username[0],token[0]).then(data => {getTaskList()})
      setNewTodoText("")
   }


   const progressTodo = () =>{
      /**affiche les todos pas encore réalisés cad todos non cochés*/
      undoneTasks(token[0], username[0]).
      then(data => {
         setTodos(data)
      })

   }


   const solvedTodo = () =>{
      /**affiche les todos dont l'exécution est terminée cad todos cochés*/
      doneTasks(token[0], username[0]).
      then(data => {
         setTodos(data)
      })

   }

   const allTodos = () =>{
      /**affiche les todos qu'ils soient cochés ou non*/
      getTaskList()
   }


   const checkAllTodos = () =>{
      checkAll(token[0] ,username[0]).then(data => {getTaskList()})
   }

   const uncheckAllTodos = () =>{
      unCheckAll(token[0], username[0]).then(data => {getTaskList()})
}

   const deleteTodo = (id) => {
      /**permet la suppression d'un item donné en paramètre */
      deleteTask(id,token[0]).then(getTaskList)
   }

   return (
      <View >

      <View>

         <FlatList
            style={styles.listTask}
            data={todos}
            renderItem={({item}) => <TodoItem item={item} getTaskList={getTaskList} deleteTodo={deleteTodo} updateCountItemDone={updateCountItemDone}
            checkAllTodos={checkAllTodos} uncheckAllTodos={uncheckAllTodos}/>}  />

         <Text>
            Le nombre d'item dans TodoData est {todosScreen.length}
            {'\n'} 
            Le nombre d'item realisé est {count}

         </Text>
      </View>

         <TextInput
            onChangeText={setNewTodoText}
            placeholder='saisir ici un nouvel item'
            onSubmitEditing={addNewTodo}

            value={newTodoText}
         />

         <View style = {styles.content}>
         <Button color='#B9F4FE' margin='80' title="Créer nouvel item" onPress={addNewTodo}>New</Button>

         <Button color='#88EBFF' margin='80' title="Cocher tout" onPress={checkAllTodos}>New</Button>

         <Button color='#3FE1FF' margin='80' title="Décocher tout" onPress={uncheckAllTodos}>New</Button>

         <Button color='#00BADC'  margin='80'title="Todos résolus" onPress={solvedTodo}>New</Button>

         <Button color='#0091AB' margin='80' title="Todos en cours" onPress={progressTodo}>New</Button>

         <Button color='#007C92'style margin='80' title="Tous les Todos (confondus)" onPress={allTodos}>New</Button>
         </View>




        </View>








   );
}



const styles = StyleSheet.create({
   content: {
      margin : 100,
      backgroundColor : 'steelblue',
      marginBottom: 12 
   },
   listTask: {
      margin : 125,
      justifyContent : 'center'


   }
})
