// npm i --save @react-navigation/bottom-tabs @react-navigation/native 

import React from 'react'
import { View, Text } from 'react-native'
import { NavigationContainer } from '@react-navigation/native'
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs'
import Icon from 'react-native-vector-icons/Ionicons'

import TodoListsScreen from '../Screen/TodoListsScreen'
import HomeScreen from '../Screen/HomeScreen'
import SignInScreen from '../Screen/SignInScreen'
import SignUpScreen from '../Screen/SignUpScreen'
import SignOutScreen from '../Screen/SignOutScreen'

import { TokenContext } from '../Context/Context'

const Tab = createBottomTabNavigator()

export default function Navigation () {
  return (
    <TokenContext.Consumer>
      {([token, setToken]) => (
        <NavigationContainer>
          {token == null ? (
            <Tab.Navigator>
              <Tab.Screen name='Connexion' component={SignInScreen}  />
              <Tab.Screen name='Inscription' component={SignUpScreen} />
            </Tab.Navigator>
          ) : (
            <Tab.Navigator>
              <Tab.Screen name='Accueil' component={HomeScreen} options={{tabBarLabel: 'Accueil', tabBarIcon:({focused}) => (<Icon name = 'home' size = '25' color = 'steelblue'/>),}}/>
              <Tab.Screen name='Liste des tâches' component={TodoListsScreen} options={{tabBarLabel: 'Liste des tâches'}}/>
              <Tab.Screen name='Deconnexion' component={SignOutScreen} options={{tabBarLabel: 'Deconnexion'}}/>
            </Tab.Navigator>
          )}
        </NavigationContainer>
      )}
    </TokenContext.Consumer>
  )
}
