import React, {useState, useEffect } from "react";
import { StyleSheet, View, TextInput, Button, Text, FlatList, Switch } from 'react-native';


import TodoItem from './TodoItem';

export default function TodoList(){
   /** initialiser todos afin de pouvoir l'utiliser avec les changements d'états*/
   const [todos, setTodos]= useState(todoData)
   const [todosScreen, setTodosScreen] = useState(todoData)
   const [count, setCount]= useState(todos.filter(item => item.done).length)
   const [newTodoText, setNewTodoText] = useState("")


   const updateCountItemDone = (id) =>{
      /**compte les fonctions realisées et actualise au fur et à mesure */
      const newTodos = todos.map((todo) => {
      if (todo.id === id) {
         todo.done = !todo.done;
      } 
      return todo;
      });
      console.log("todos", newTodos)

      setTodos(newTodos);
      setTodosScreen(newTodos)

      setCount(newTodos.filter(item => item.done).length);

   }




   const addNewTodo = () =>{
      /**ajoute un nouvel item à notre liste de taches */
      const newTodos = [...todos, { id: Math.max(...todos.map(item => item.id)) + 1, content: newTodoText, done: false }]
      setTodos(newTodos)
      setTodosScreen(newTodos)
      setNewTodoText("")
   }


   const progressTodo = () =>{
      /**affiche les todos pas encore réalisés cad todos non cochés*/
      const solved = todos.filter(item => item.done == false)
      setTodosScreen(solved);
     }

   // const allTodo = () =>{
   //    /**affiche tous les todos sans tenir compte de leur état de realisation*/
   //    setTodos(todos)  
   // }


   const solvedTodo = () =>{
      /**affiche les todos dont l'exécution est terminée cad todos cochés*/
      const solved = todos.filter(item => item.done == true)
      setTodosScreen(solved);
   }

   const allTodos = () =>{
      /**affiche les todos qu'ils soient cochés ou non*/
      const solved = todos
      setTodosScreen(solved);
   }


   const checkAllTodos = () =>{
        const newTodos = todos.map((todo) => {
            if (todo.done === false)
                todo.done = true
            return todo

            });
            setTodos(newTodos)
            setTodosScreen(newTodos)
            setCount(newTodos.filter(item => item.done).length)
   }

   const uncheckAllTodos = () =>{
    const newTodos = todos.map((todo) => {
        if (todo.done === true)
            todo.done = false
        return todo

        });
        setTodos(newTodos)
        setTodosScreen(newTodos)
        setCount(newTodos.filter(item => item.done).length)
}

   const deleteTodo = (id) => {
      /**permet la suppression d'un item donné en paramètre */
      const newTodos = todos.filter(item => item.id != id)
      setTodos(newTodos)
      setTodosScreen(newTodos)

      console.log(newTodos)
   }



   return (
      <View>

         <FlatList
            style={{ paddingLeft: 10 }}
            data={todosScreen}
            renderItem={({item}) => <TodoItem item={item} deleteTodo={deleteTodo} updateCountItemDone={updateCountItemDone}
            checkAllTodos={checkAllTodos} uncheckAllTodos={uncheckAllTodos}/>}  />

         <Text>
            Le nombre d'item dans TodoData est {todos.length}
            {'\n'} 
            Le nombre d'item realisé est {count}

         </Text>


         <TextInput
            onChangeText={setNewTodoText}
            placeholder='saisir ici un nouvel item'
            onSubmitEditing={addNewTodo}

            value={newTodoText}
         />

         <Button color='#32612D' title="Créer nouvel item" onPress={addNewTodo}>New</Button>

         <Button color='#32612D' title="Cocher tout" onPress={checkAllTodos}>New</Button>

         <Button color='#32612D' title="Décocher tout" onPress={uncheckAllTodos}>New</Button>

         <Button color='#DD571C' title="Todos résolus" onPress={solvedTodo}>New</Button>

         <Button color='#2C3E4C' title="Todos en cours" onPress={progressTodo}>New</Button>

         <Button color='#0080FF' title="Tous les Todos (confondus)" onPress={allTodos}>New</Button>


         {/* 


         <Button color='#1FD655' title="Tout afficher" onPress={allTodo}>New</Button> */}


      </View>






   );
}

const styles = StyleSheet.create({
   content: {
      flexDirection: 'row',
   },
   button: {
      marginLeft: 10,
      width: 150,


   }
})
