import React, { useState, useEffect} from "react";
import { Image, View, Text, StyleSheet, Switch, TouchableOpacity } from 'react-native';

export default function TodoItem(props) {
    const [done, setDone] = useState(props.item.done);

    useEffect(() => {
        setDone(props.item.done)
    },
        [props.item.done]
    );

    useEffect(() => {
        props.getTasklist()
    },
        [props.item.done]
    );





    return (
        <View style={styles.content}>

            {/* <Switch value={done} onValueChange={() => setDone(state => !state)}/> */}

            <Switch value={done} onValueChange={(state) => {setDone(state); props.updateCountItemDone(props.item.id)}}/>

            <Text style={[styles.text_item, { textDecorationLine: done ? 'line-through' : 'none' }]}>{props.item.title}</Text>

            <TouchableOpacity onPress={() => (props.deleteTodo(props.item.id))}>

                <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />

            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({
    content: {
        flexDirection: 'row'
    },
    text_item: {
        marginLeft: 10,
        width: 150
    }
})
